'use strict';

var lab = require('lab');

lab.experiment('the module', function() {
    var anode;

    lab.before(function(done) {
        anode = require('../');
        done();
    });

    lab.test('exports an object with an .authenticate function', function (done) {
        lab.expect(anode).to.be.a('object');
        lab.expect(anode).to.have.property('authenticate');
        lab.expect(anode.authenticate).to.be.a('function');
        done();
    });

    lab.test('exports an object with a 3-arity register method', function(done) {
        lab.expect(anode).to.have.property('register');
        lab.expect(anode.register).to.be.a('function');
        lab.expect(anode.register.length).to.eql(3);
        done();
    });
});
